const englishNote = (category, style, length, i) => `Generated ${style} ${length} pickup line for ${category} (#${i + 1})`;

const buildRecord = (category, style, length, index) => {
  const info = CATEGORY_INFO[category];
  const tone = STYLE_TONE[style];
  const token = info.tokens[index % info.tokens.length];
  const token2 = info.tokens[(index + 1) % info.tokens.length];

  const templateBank = length === 'short'
    ? SHORT_TEMPLATES[style]
    : length === 'medium'
      ? MEDIUM_TEMPLATES[style]
      : LONG_TEMPLATES[style];

  const lineBuilder = templateBank[index % templateBank.length];
  const burmeseText = lineBuilder({
    noun: info.noun,
    token,
    token2,
    icon: info.icon,
    emo: tone.emoji
  });

  return {
    category,
    style,
    length,
    burmese_text: burmeseText,
    english_source_text: englishNote(category, style, length, index),
    source_url: 'internal://generated/curated-v1',
    license_note: 'internal original generated content',
    quality_score: style === 'sarcastic' ? 76 : 82,
    safety_score: 96,
    tags: [category, style, length, tone.mood],
    review_status: 'approved',
    language: 'my'
  };
};

const run = async () => {
  const rows = [];

  for (const category of CATEGORIES) {
    for (const style of STYLES) {
      for (const length of LENGTHS) {
        for (let i = 0; i < 3; i += 1) {
          rows.push(buildRecord(category, style, length, i));
        }
      }
    }
  }

  const outputPath = path.resolve(__dirname, '../../data/staging/curated_travel_new_categories_v1.json');
  await fs.writeFile(outputPath, JSON.stringify(rows, null, 2), 'utf8');

  console.log(`Generated ${rows.length} lines`);
  console.log(outputPath);
};

run().catch((error) => {
  console.error(error);
  process.exit(1);
});
