const ENGLISH_HINTS = [
  'Are you Siri? Because you autocomplete me.',
  "Are you a charger? Because I'm dying without you.",
  "Are you a keyboard? Because you might just be my type.",
  'Are you Google? You have everything I search for.',
  "Are you a WiFi router? Because I'm feeling a connection.",
  'Are you a magician? Because when I look at you, everyone else disappears.',
  'Are you a time traveler? Because I can see you in my future.'
];

const pickTemplate = (style, length, i) => {
  const bank = length === 'short' ? SHORT[style] : length === 'medium' ? MEDIUM[style] : LONG[style];
  return bank[i % bank.length];
};

const buildLine = (category, style, length, i) => {
  const c = CAT[category];
  const tone = TONE[style];
  const line = pickTemplate(style, length, i)({ t: c.topic, k: c.keyword, e: c.emoji, emo: tone.emo });
  const sourceUrl = REF_LINKS[i % REF_LINKS.length];

  return {
    category,
    style,
    length,
    burmese_text: line,
    english_source_text: ENGLISH_HINTS[(i + category.length + style.length) % ENGLISH_HINTS.length],
    source_url: sourceUrl,
    license_note: 'reference-inspired rewrite in Burmese (human-edited template)',
    quality_score: tone.quality,
    safety_score: 97,
    tags: [category, style, length, 'reference-inspired', 'burmese-rewrite'],
    review_status: 'approved',
    language: 'my'
  };
};

const run = async () => {
  const rows = [];

  for (const category of CATEGORIES) {
    for (const style of STYLES) {
      for (const length of LENGTHS) {
        for (let i = 0; i < 3; i += 1) {
          rows.push(buildLine(category, style, length, i));
        }
      }
    }
  }

  const outputPath = path.resolve(__dirname, '../../data/staging/curated_reference_v2.json');
  await fs.writeFile(outputPath, JSON.stringify(rows, null, 2), 'utf8');

  console.log(`Generated ${rows.length} reference-inspired lines`);
  console.log(outputPath);
};

run().catch((error) => {
  console.error(error);
  process.exit(1);
});
